
public class whileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		do
		{
			int j=1;
			while(j<=5)
			{
				System.out.print("  Vishal  ");
				j++;
			}
			System.out.println();
			i++;
		}while(i<=5);
		System.out.println("  Shindhe  ");

	}

}
